import classes from "./Input.module.css";

const Input = (props) => {

    const inputRef = useRef();

    return (
        <div
            className={`${classes.control} ${emailState.isValid === false ? classes.invalid : ''
                }`}
        >
            <label htmlFor={props.id}>{props.label}</label>
            <input
                ref={inputRef}
                type={props.type}
                id={props.id}
                value={props.value}
                onChange={props.onChange}
                onBlur={props.onBlur}
            />
        </div>
    );
}
